(function($) {

// Use this variable to set up the common and page specific functions. If you 
// rename this variable, you will also need to rename the namespace below.
var Roots = {
  // All pages
  common: {
    init: function() {
      // JavaScript to be fired on all pages
      // Dynamically and lazily embed the product background image
      if (typeof lifetrak !== 'undefined') {
        if (typeof lifetrak.background_image !== 'undefined') {
          $('.outer-wrap').css('background', "url('" + lifetrak.background_image + "') no-repeat right top");
        }
        if (typeof lifetrak.background_color !== 'undefined') {
          $('.outer-wrap').css('background-color', lifetrak.background_color);
        }
      }
      // Prevents typing/pasting/inputing of quantity field to enforce only using the plus/minus buttons
      $('input.qty').on('keydown keypress paste', function(e){
        e.preventDefault();
        return false;
      });
    }
  },
  // Home page
  home: {
    init: function() {
      
    }
  },
  // About us page, note the change from about-us to about_us.
  about_us: {
    init: function() {
      // JavaScript to be fired on the about us page
    }
  },
  // FAQ page
  page_id_71: {
    init: function() {
      if ($('.faq-hor').length > 0) {
        var firsttime = true;
        $('.faq-hor .su-tabs-nav span').off();
        $('.faq-hor .su-tabs-nav span').on('click', function(){
          var me = $(this);
          var cl = $.trim(me.attr('class'));
          var set = ((cl.split(' '))[0].split('-'))[1];

          if ( !firsttime && $(window).innerWidth() < 490 ) {
            $("html, body").animate({
                scrollTop: $(".vert-top-anchor").offset().top
            }, 1000, 'swing', function(){
              $('.faq-vert:visible').fadeOut('fast', function(){
                $('.' + set).fadeIn();
              });
            });
          } else {
            firsttime = false;
            $('.faq-vert:visible').hide();
            $('.' + set).show();
          }
        });
      }
      if ($('.accordion-toggle').length > 0) {
        $('.accordion-toggle').on('click', function(){
          var me = $(this);
          var content = me.parent().next('.accordion-body');
          content.slideToggle();
        });
      }
    }
  },
  // Product pages
  single_product: {
    init: function(){
      // Make 3d iframe height match the width
      if ($('.product-3d-wrap iframe').length > 0) {
        var resizing = null;
        var adjust_3d_iframe_height = function(){
          if (resizing !== null) {
            clearTimeout(resizing);
          }
          resizing = setTimeout(function(){
            var width = $('.product-3d-wrap iframe').outerWidth();
            $('.product-3d-wrap iframe').height(width);
          }, 300);
        };
        $(window).on('resize', adjust_3d_iframe_height);
        adjust_3d_iframe_height();
      }
    }
  },
  // Fitness Tracker page
  page_id_105: {
    init: function(){
      //alert('test');
      var questionnaire = {
        /**
         * Stores all DOM elements here
         */
        'el': {},
        /**
         * Current index of the question
         */
        'current_index': 0,
        /**
         * How many questions there are
         */
        'item_count': 0,
        /**
         * Array of scroller y-axis points to calculate it's position depending on which
         * question we're on, each index corresponds to Y position to the question we're on
         */
        'scroller_positions': [],
        /**
         * Variable to store window resize setTimeout object
         */
        'resizing': null,
        /**
         * Answer storage
         */
        'answer_store': {},
        /**
         * Radio store, a helper object
         */
        'radio_store': {},
        /**
         * To make sure that the user has selected an answer before going to the next question
         */
        'selected_answer': false,
        /**
         * Executes animation of going to the next question
         * @return void
         */
        'go_to_question': function(index){
          // This is what sets the scroller's position oh yea...
          if (index > 0) {
            this.el.scroller.css('top', '-' + this.scroller_positions[index] + 'px')  ;
          } else {
            this.el.scroller.css('top', '0')  ;
          }
          // Set new index
          this.current_index = index;
          // The current question we're on
          this.el.current_ques = $(this.el.ques[this.current_index]);
          // The next question we're gonna go to
          this.el.next_ques = $(this.el.ques[this.current_index + 1]);
          // Set up the questionnaire so that only two show
          this.calculate_question_heights();
          // Fade next question
          this.fade_next_question();
          // Unfade current question
          this.unfade_current_question();
          // Highlight appropriate numbered paginator
          //this.highlight_number();
        },
        /**
         * Changes opacity of current question to 1
         * @return void
         */
        'unfade_current_question': function(){
          this.el.current_ques.removeClass('faded');
        },
        /**
         * Fades the next question
         * @return void
         */
        'fade_next_question': function(){
          this.el.next_ques.addClass('faded');
        },
        /**
         * Go to next question
         */
        'go_to_next_question': function(){
          var self = questionnaire;

          // if deferred filter then do filtering here
          if (self.deferred) {
            self.deferred = false;
            self.filter_products();
          }

          if (!self.selected_answer) {
            return false;
          }
          // reset it to false for the next question
          self.selected_answer = false;
          if (self.current_index + 1 < self.item_count) {
            self.go_to_question(self.current_index + 1);
          }
          // check if it's the end
          if (self.current_index + 1 === self.item_count) {
            $('.f-que-pager .next').hide();
          }
        },
        /**
         * Go to previous question
         * Currently deprecated
         */
        'go_to_previous_question': function(){
          var self = questionnaire;
          if (self.current_index - 1 > -1) {
            self.go_to_question(self.current_index - 1);
          }
        },
        /**
         * Highlight appropriate numbered paginator
         */
        /*'highlight_number': function(){
          this.el.page_links.filter('.selected').removeClass('selected');
          $(this.el.page_links[this.current_index]).addClass('selected');
        },*/
        /**
         * Calculate question scroller's positions for easy navigation!
         */
        'calculate_scroller': function(){
          var accumulator = 0;
          for (var i = 0; i < this.item_count; i++) {
            if (i - 1 > -1) {
              accumulator += $(this.el.ques[i - 1]).outerHeight(true);
              this.scroller_positions[i] = accumulator;
            } else {
              // This is gonna be the first question, which is just top: 0px
              this.scroller_positions[i] = i;
            }
          }
          //console.log(this.scroller_positions);
        },
        /**
         * Adjust question heights
         */
        'calculate_question_heights': function(){
          var height = this.el.current_ques.outerHeight(true) + this.el.next_ques.outerHeight(true);
          this.el.container.height( height );
        },
        'filter_products': function() {
          // Remove the faded class in order to reply the filters according to the rules
          this.el.products.removeClass('faded');
          /**
           * Magic: so the way it works is, it will start looping through the answer_store object, each having an array of the
           * product classes and will simply just apply the faded class to these products. The reason we're
           * doing it on a re-iterative way is to account for the unchecking of checkboxes, which becomes super
           * tricky business to deal with in terms of restoring previous filter states of the products
           */
          for(var i in this.answer_store) {
            for(var j in this.answer_store[i]) {
              $('.product-sug.' + this.answer_store[i][j]).addClass('faded');
            }
          }
        },
        /**
         * This is where all event handlers are
         */
        'events': {
          'checked_answer': function(e) {
            var self = questionnaire;
            var me = $(this);
            var question = me.parent().parent().parent();

            // This disallows the selection of a question that we're not on yet
            if (question.hasClass('faded')) {

              e.preventDefault();
              me.prop('checked', false);
              return false;

            } else {

              // Selects all checkboxes/radio of the current question we're on
              var chk_alongside = me.parent().parent().find('.f-que-chk');
              // Detects if it's a radio button or not
              var single_select = me.attr('type') === 'radio';
              // Grabs the ID attribute of the checkbox/radio
              var ans = me.attr('id');
              // Grabs what question we're on (the index starts at zero)
              var qnum = me.data('qnum');
              // Contains the classes of the products to filter out in array format
              var filters = me.data('filterout');

              if (this.checked) {
                // This is to indicate that we've selected an answer, and will allow us to go to the next question
                self.selected_answer = true;

                // Make sure that the previous radio button selected is no longer in the answer_store object
                if (single_select) {
                  if (self.radio_store['q' + qnum]) {
                    delete(self.answer_store[self.radio_store['q' + qnum]]);
                  }
                  // We store the answer selected just in case the user changes his answer
                  self.radio_store['q' + qnum] = ans;
                }

                // If the current checkbox/radio actually filters out something, then filter it
                if (filters) {
                  self.answer_store[ans] = filters;
                }

                

                // Specific rules for specific checkboxes/radios that do alerts
                if (qnum === 3) {
                  if (ans === 'ans_3_0' || ans === 'ans_3_1') {
                    alert("You mentioned that heart rate is not important to you. We just wanted you to know that heart rate is the top way to measure the intensity of your activity and using it results in more accurate calorie burn estimates.  This is why all our products use heart rate as a method of ensuring accuracy and a measure of health. Don't worry! LifeTrak products are intuitive and you don't need to understand calculating heart rate or calorie burn  - we do the work for you!");
                  }
                } else if (qnum === 6) {
                  if (ans !== 'ans_6_0') {
                    alert("You mentioned that monitoring your light exposure is not important to you.  We just wanted to let you know that light plays a big part in our lives and that the scientific and medical communities have proven that it affects our mood, energy level and daily rhythms, including sleep.");
                  }
                } else if (qnum === 7) {
                  // if the previous questions were all NO, then filter out the brite
                  if ($('#ans_5_1').is(':checked') && $('#ans_6_1').is(':checked') && $('#ans_7_1').is(':checked')) {
                  	// override and fade out brite r450 automatically
                  	$('.product-sug.brite-r450').addClass('faded');
                    self.answer_store[ans] = ['brite-r450'];
                    // to indicate that this data was inserted dynamically and that it should go away when the quiz is reset
                    me.data('temp', true);
                    // add the data to the data attribute
                    me.data('filterout', ['brite-r450']);
                    self.deferred = true;
                  }
                } else if (qnum === 9) {
                  if (ans === 'ans_9_0') {
                    alert("For Move C300 & Zone C410:\nCOMPATIBLE WITH: iOs 7 and higher: iPhone 4S, 5, 5C, 5S and beyond; iPad 3, 4; iPad Air; iPad Mini, Retina; iPod Touch 5\nAnd\nCOMPATIBLE WITH: The following Android Devices: Samsung Galaxy 4, Galaxy 5 and Note III, LG Nexus 5, Motorola Moto G and HTC One M8\n\nFor Brite R450:\nCOMPATIBLE WITH: iOs 7 and higher: iPhone 4S, 5, 5C, 5S and beyond; iPad 4; iPad Air; iPad Mini, Retina; iPod Touch 5\nAnd\nCOMPATIBLE WITH: The following Android Devices: select smartphones by the end of 2014.  Check back for details.");
                  }
                  if ($('#ans_8_0').is(':checked') && $('#ans_9_1').is(':checked')) {
                  	$('.product-sug.zone-c410').addClass('faded');
                  	self.answer_store[ans] = ['zone-c410'];
                  	me.data('temp', true);
                  	me.data('filterout',['zone-c410']);
                  	self.deferred = true;
                  	$('.product-sug.move-c300').addClass('faded');
                  }
                } else if (qnum === 10) {
                  if (!$('#ans_10_2').is(':checked')) {
                    alert("No matter your level of comfort with technology, LifeTrak products are designed to be easy to set up, easy to use and easy to understand. Even beginners will enjoy the benefits of our advanced technology without stress!");
                  }
                }
                
                if (!self.deferred) {
                  self.filter_products();
                }
                // If the current question was radio buttons, selecting one will move the user to the next question automatically
                /*if (single_select) {
                  // Advance to next question
                  self.go_to_next_question();
                }*/

              } else {

                // If the checkbox was unselected, we need to detect whether there are no answers selected anymore
                if (chk_alongside.filter(':checked').length === 0) {
                  self.selected_answer = false;
                }

                // This will do the magic of undoing the filtering of the products
                if (filters) {
                  delete(self.answer_store[ans]);
                  self.filter_products();
                }
              }
            }
          },
          /**
           * This event handler is deprecated and no longer used because we're no longer using the
           * numbered paginator, but we'll keep it just in case they want it back -_-
           */
          'numbered_page': function() {
            var self = questionnaire;
            self.go_to_question($(this).index('.fitness-q-page-nums .num'));
          },
          /**
           * This restarts the questionnaire
           */
          'start_over': function(){
            var self = questionnaire;
            self.go_to_question(0);
            self.answer_store = {};
            self.radio_store = {};
            $('.f-que-chk[data-temp]').removeAttr('data-filterout');
            $('.f-que-chk[data-temp]').removeAttr('data-temp');
            // Clicks the first numbered thing
            //$($('.fitness-q-page-nums .num')[0]).click();
            // Reset dropdowns
            //$('.f-que-sel').val('');
            // Uncheck all checkboxes/radios
            $('.f-que-chk').prop('checked', false);
            //self.answers = {};
            self.el.products.removeClass('faded');
            $('.f-que-pager .next').show();
          },
          /**
           * This is the window resize handler, which will recalculate the height of the questionnaire,
           * and properly position the questions so that it looks right ALL THE TIME
           */
          'resizing': function(){
            var self = questionnaire;
            // This is to throttle the window resize event to not fire all the time, specially if the
            // user is still dragging the window
            if (self.resizing) {
              clearTimeout(self.resizing);
            }
            self.resizing = setTimeout(function(){
              self.calculate_question_heights();
              self.calculate_scroller();
              self.go_to_question(self.current_index);
            }, 300);
          }
        },
        /**
         * Function that initializes the questionnaire
         * @return void
         */
        'initialize': function(){
          // All the questions
          this.el.ques = $('.f-que-ans');
          // Store number of questions there are
          this.item_count = this.el.ques.length;
          // Check that there are some questions
          if (this.item_count > 0) {
            // Initialize current index
            this.current_index = 0;
            // Store the quetsionnaire container 
            this.el.container = $('.fitness-questions');
            // Store the questionnaire scroller wrapper element
            this.el.scroller = $('.f-que-scroller');
            // Store numbered paginators 
            //this.el.page_links = $('.fitness-q-page-nums .num');
            // Store the products
            this.el.products = $('.product-sug');

            // Attach prev/next to event handler
            //$('.f-que-pager .prev').on('click', this.go_to_previous_question);
            $('.f-que-pager .prev').on('click', this.events.start_over);
            $('.f-que-pager .next').on('click', this.go_to_next_question);
            // Attach numbered paginator to event handler
            //this.el.page_links.on('click', this.events.numbered_page);
            // Attach the start over button
            //$('.fitness-q-page-nums .start-over').on('click', this.events.start_over);
            // Attach current question's checkboxes to the event handler!
            $('.f-que-chk').on('click', this.events.checked_answer);

            // Calculate the scroller's Y positions for navigation
            this.calculate_scroller();
            // If the window resizes, we need to recalculate scroller and maybe other stuff
            $(window).on('resize', this.events.resizing);
            // This starts it off!
            this.go_to_question(0);
          }
        }
      };
      // Let's get started!
      questionnaire.initialize();

      // Do isotope layout on the grid below
      $('.products').imagesLoaded(function(){
        $('.products .row').isotope({
          itemSelector: '.product-sug',
          layoutMode: 'fitRows'
        });
      });
      
    }
  }
};

// The routing fires all common scripts, followed by the page specific scripts.
// Add additional events for more control over timing e.g. a finalize event
var UTIL = {
  fire: function(func, funcname, args) {
    var namespace = Roots;
    funcname = (funcname === undefined) ? 'init' : funcname;
    if (func !== '' && namespace[func] && typeof namespace[func][funcname] === 'function') {
      namespace[func][funcname](args);
    }
  },
  loadEvents: function() {
    UTIL.fire('common');

    $.each(document.body.className.replace(/-/g, '_').split(/\s+/),function(i,classnm) {
      UTIL.fire(classnm);
    });
  }
};

$(document).ready(UTIL.loadEvents);

})(jQuery); // Fully reference jQuery after this point.
